VertexShader.Raw:
#version 130

uniform mat4 modelview_matrix;
uniform mat4 projection_matrix;
in vec3 a_Vertex;

void main(void)
{
    vec4 pos = modelview_matrix * vec4(a_Vertex, 1.0);
    gl_Position = projection_matrix * pos;
}

FragmentShader.Raw:
#version 130

precision highp float;

uniform vec4 color;

void main(void)
{
    gl_FragColor = color;
}
